import React, { useState, useEffect, useRef } from 'react';
import './App.css';
import sopData from './sops.json';

function App() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const apiKey = "AIzaSyBPk4NYw_5enfX5-OJxYN14haaxFoQxiPM"; // <-- your API key here
  const messagesEndRef = useRef(null);

  useEffect(() => {
    setMessages([{
      text: "Hello! I'm THE SOP AI. How can I help you today with store procedures?",
      sender: 'ai'
    }]);
  }, []);

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  const createContext = () => {
    let context = "You are THE SOP AI, an expert assistant trained on the company's Standard Operating Procedures.\n\n";

    sopData.sops.forEach(sop => {
      context += `CATEGORY: ${sop.category}\n`;
      context += `TITLE: ${sop.title}\n`;
      context += `CONTENT:\n${sop.content.join("\n")}\n\n`;
    });

    return context;
  };


  const sendToGemini = async (userQuestion) => {
    try {
      const response = await fetch("https://generativelanguage.googleapis.com/v1beta/models/gemini-2.0-flash:generateContent?key=" + apiKey, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          contents: [
            {
              parts: [
                {
                  text: createContext() + `Question: ${userQuestion}\nDo NOT use markdown format! Responnd from the point of view of a super chill stoner. Only respond in plain text with NO formatting. Answer:`
                }
              ]
            }
          ]
        })
      });

      const data = await response.json();

      if (data.error) {
        throw new Error(data.error.message || "Error with Gemini API");
      }

      return data.candidates[0].content.parts[0].text;
    } catch (error) {
      console.error('Error with Gemini API:', error);
      return "Sorry, I encountered an error while processing your request. Please try again later.";
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!input.trim()) return;

    const userMessage = { text: input, sender: 'user' };
    setMessages(prev => [...prev, userMessage]);
    setInput('');
    setIsLoading(true);

    const aiResponse = await sendToGemini(input);
    const aiMessage = { text: aiResponse, sender: 'ai' };
    setMessages(prev => [...prev, aiMessage]);
    setIsLoading(false);
  };

  return (
    <div className="app">
      <div className="chat-container">
        <header className="chat-header">
          <h1>THE SOP AI</h1>
          <p>Your retail assistant for Standard Operating Procedures</p>
        </header>

        <div className="messages-area">
          {messages.map((message, index) => (
            <div key={index} className={`message ${message.sender}`}>
              {message.text}
            </div>
          ))}
          {isLoading && (
            <div className="message ai loading">
              <div className="typing-indicator">
                <span></span>
                <span></span>
                <span></span>
              </div>
            </div>
          )}
          <div ref={messagesEndRef} />
        </div>

        <form onSubmit={handleSubmit} className="input-form">
          <input
            type="text"
            value={input}
            onChange={(e) => setInput(e.target.value)}
            placeholder="Ask about store procedures..."
            disabled={isLoading}
          />
          <button type="submit" disabled={isLoading || !input.trim()}>
            Send
          </button>
        </form>
      </div>
    </div>
  );
}

export default App;
